import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [userID, setUserID] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const postData = async () => {
    try {
      const response = await axios.post('http://localhost:1001/userService/Login', {
        userID: userID,
        password: password,
      });

      const success = response.status; // 서버에서 반환한 토큰
      const token = response.data; // 성공 여부

      if (success) {
        localStorage.setItem('jwtToken', token); // 토큰을 로컬 스토리지에 저장
        navigate('/Test'); // 로그인 성공 시 성공 페이지로 이동
      } else {
        console.error('요청 실패: ', response.data); // 실패 시 콘솔에 로그
      }

    } catch (error) {
      console.error('데이터 전송 실패: ', error); // 에러 발생 시 콘솔에 로그
    }
  };

  const joinPage = () => {
    navigate('./Signup'); // 회원가입 페이지로 이동
  };

  return (
    <div>
      <h2>로그인</h2>
      <input
        type="text"
        placeholder="아이디"
        value={userID}
        onChange={(e) => setUserID(e.target.value)}
      />
      <input
        type="password"
        placeholder="비밀번호"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={postData}>로그인</button>
      <button onClick={joinPage}>회원가입</button>
    </div>
  );
}

export default Login;
