import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [userID, setUserID] = useState('');  
  const [password, setPassword] = useState('');    
  const [setResponseMessage] = useState('');
  const navigate = useNavigate();

  const postData = async () => {
    try {
      const response = await axios.post('http://localhost:1001/userService/Login', {
        userID,  password
      });

      if (response.data.success) {
        navigate('./test');
      } else {
        setResponseMessage('요청에 실패했습니다.');
      }

    } catch (error) {
      console.error('데이터 전송 실패: ', error);
      setResponseMessage('데이터 전송에 실패했습니다.');
    }
  };

  const joinPage = async () => {
    navigate('./signup');
  };

  return (
    <div>
      <h2>회원 정보 입력</h2>

      <label>
        ID:
        <input
          type="text"
          value={userID}
          onChange={(e) => setUserID(e.target.value)} // 입력된 값을 상태로 저장
        />
      </label>
      <br />
      <label>
        PW:
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // 입력된 값을 상태로 저장
        />
      </label>
      <br />
      {/* 서버로 데이터를 전송하는 버튼 */}
      <button onClick={postData}>Login</button>
      <button onClick={joinPage}>Signup</button>
    </div>
  );
}

export default Login;
