import React, { useState } from 'react';
import axios from 'axios';
//import { useNavigate } from 'react-router-dom';

function Login() {
  const [responseMessage, setResponseMessage] = useState('');
  const navigate = useNavigate(); // 페이지 이동을 위한 useNavigate 사용

  const postData = async () => {
    try {
      // axios를 사용하여 POST 요청 보내기
      const response = await axios.post('http://localhost:1001/userService/servicePage', {
        name: 'John Doe',  // 서버에 전달할 데이터
        age: 30
      });

      // 서버에서 성공 응답을 받으면 페이지 이동
      if (response.data.success) {
        navigate('/success-page');  // 긍정적인 응답이면 다른 페이지로 이동
      } else {
        setResponseMessage('요청에 실패했습니다.');
      }

    } catch (error) {
      console.error('데이터 전송 실패: ', error);
      setResponseMessage('데이터 전송에 실패했습니다.');
    }
  };

  return (
    <div>
      <h2>서버로부터 받은 메시지:</h2>
      <p>{responseMessage}</p>
      <button onClick={postData}>데이터 전송</button>
    </div>
  );
}

export default Login;
